<?phprequire_once('inc/functions/general.php');include_once 'inc/functions.php';require_once 'wconfig.php';error_reporting(E_ALL | E_STRICT);$Queue = new QuickBooks_WebConnector_Queue($dsn, $qbwc_user);$newid = rand();if(isset($_GET['qi'])){		switch($_GET['qi']){		case 'nitem':			$Queue->enqueue(QUICKBOOKS_QUERY_NONINVENTORYITEM, $newid);			echo 'Query non_inventory item XML - prepared. Run web Connector to update XML';		break;		case 'iitem':			$Queue->enqueue(QUICKBOOKS_QUERY_INVENTORYITEM, $newid);			echo 'Query inventory item XML - prepared. Run web Connector to update XML';		break;		case 'customer':			$Queue->enqueue(QUICKBOOKS_QUERY_CUSTOMER, $newid);			echo 'Query customer XML - prepared. Run web Connector to update XML';		break;		case 'estimate':			$Queue->enqueue(QUICKBOOKS_QUERY_ESTIMATE, $newid);			echo 'Query estimate XML - prepared. Run web Connector to update XML';		break;		case 'order':			$Queue->enqueue(QUICKBOOKS_QUERY_SALESORDER, $newid);			echo 'Query order XML - prepared. Run web Connector to update XML';		break;		case 'payment':			$Queue->enqueue(QUICKBOOKS_QUERY_PAYMENTITEM, $newid);			echo 'Query payment XML - prepared. Run web Connector to update XML';		break;		case 'porder':			$Queue->enqueue(QUICKBOOKS_IMPORT_PURCHASEORDER, $newid);			echo 'Query purchase order XML - prepared. Run web Connector to update XML';		break;		default:			echo 'No option selected. ';		break;		}	}exit;?>