<?phpinclude_once('inc/functions/general.php');include_once 'inc/functions.php';error_reporting(E_ALL | E_STRICT);include_once 'inc/functions.php';require_once 'wconfig.php';error_reporting(E_ERROR | E_WARNING | E_PARSE);	if(isset($_GET['io'])){	$ords = '';	$io = $_GET['io'];$args = array(    'limit' => -1,	'meta_key' => array(		'_billing_last_name', 		'_billing_first_name', 		'_billing_company'		),	'meta_value' => $io,	'meta_compare'  => 'LIKE',    'orderby' => 'date',    'order'   => 'DESC');$query = new WC_Order_Query( $args );$orders = $query->get_orders();foreach($orders as $o){	$om = get_post_meta($o->id);	$ords .= '<div id="'.$o->id.'" OnClick="putorder(this);" style="cursor: pointer;">Order: '.$o->id.'&nbsp; &nbsp; &nbsp; '.$om['_billing_company'][0]. ' - '.$om['_billing_last_name'][0]. ' - '.$om['_billing_first_name'][0].'</div>';//	$od = new WC_Order($oid);}	echo $ords;	}exit;?>