<?phpinclude_once('inc/functions/general.php');include_once 'inc/functions.php';error_reporting(E_ALL | E_STRICT);include_once 'inc/functions.php';require_once 'wconfig.php';error_reporting(E_ERROR | E_WARNING | E_PARSE);if(isset($_GET['io'])){		$ords = '';	$io = $_GET['io'];	$xml1 = simplexml_load_file($_SERVER['DOCUMENT_ROOT'] . '/' . PURCHASE_ORDER_XML);	foreach($xml1->QBXMLMsgsRs->PurchaseOrderQueryRs->PurchaseOrderRet as $d){		if(preg_match("/$io/", strtolower($d->RefNumber))){			$ords .= '<div id="'.$d->RefNumber.'" OnClick="putorder(this);" style="cursor: pointer;">Order: '.$d->RefNumber.'&nbsp; &nbsp; &nbsp; '.$d->RefNumber.'</div>';			}		}	echo $ords;	}exit;?>